import React, { useEffect, useState } from 'react'

import axios from 'axios'

import '../App.css'

export default function Quote() {
    let [quote, updateQuote] = useState([])

    const apiCall = async () => {
        const data = await axios.get('https://cors-anywhere.herokuapp.com/http://api.forismatic.com/api/1.0/&format=json&lang=en')

        console.log(data.data)
    }

    useEffect(() => {
        apiCall()
    }, [])

    return (
        <>
            <div>
                <p>This will be a quote</p>
            </div>
        </>
    )
}
