import axios from 'axios'

import '../App.css'

export default function Quote() {
    let [quote, updateQuote] = useState([])
    let [ random, updateRandom ] = useState

    let randomInt = () => {
        return Math.floor(Math.random() * 1643)

        updateRandom()
    }

    const apiCall = async () => {
        const data = await axios.get('https://type.fit/api/quotes')
        console.log(data.data[0].author)




        console.log(random)
        console.log(data.data[random].author)

    }

    useEffect(() => {
        apiCall()

        updateQuote()

    }, [])

    return (
        <>
            <div>
                <p>This will be a quote</p>
            </div>
        </>
    )
}
